import UIKit

protocol SongSearchDisplayLogic: class
{
    func displaySearchResults(viewModel: SongSearch.Search.ViewModel)
    func displayRecent(viewModel: SongSearch.Recent.ViewModel)
    func displaySongs(viewModel: SongSearch.Songs.ViewModel)
}

class SongSearchViewController: UIViewController
{
    
    @IBOutlet weak var activityIndicator: UIActivityIndicatorView!
    
    @IBOutlet weak var collectionView: UICollectionView!
    var interactor: SongSearchBusinessLogic?
    var router: (NSObjectProtocol & SongSearchRoutingLogic & SongSearchDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
    {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder)
    {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup()
    {
        let viewController = self
        let interactor = SongSearchInteractor()
        let presenter = SongSearchPresenter()
        let router = SongSearchRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?)
    {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    let searchController = UISearchController(searchResultsController: nil)
    
    func initSearchController(){
        searchController.searchResultsUpdater = self
        searchController.obscuresBackgroundDuringPresentation = false
        searchController.searchBar.placeholder = "Search songs"
        searchController.searchBar.delegate = self
        
        searchController.searchBar.tintColor = UIColor.white
        searchController.searchBar.barStyle = .black
        
        navigationItem.searchController = searchController
        navigationItem.hidesSearchBarWhenScrolling = false
        definesPresentationContext = true
    }
    
    func registerNibs(){
        self.collectionView.register(UINib(nibName: "SongSearchCell", bundle: nil), forCellWithReuseIdentifier: ReuseIdentifiers.SongSearchCell)
    }
    // MARK: View lifecycle
    
    override func viewDidLoad()
    {
        super.viewDidLoad()
        
        if let flow = collectionView.collectionViewLayout as? UICollectionViewFlowLayout{
            flow.estimatedItemSize = CGSize(width: UIScreen.main.bounds.width - (8*2), height: 100)
        }
        
        initSearchController()
        registerNibs()
        activityIndicator.hidesWhenStopped = true
        activityIndicator.stopAnimating()
        self.view.backgroundColor = UIColor.dark
        songs()
    }
    
    // MARK: Do something
    
    //@IBOutlet weak var nameTextField: UITextField!
    
    func search(query: String)
    {
        activityIndicator.startAnimating()
        let request = SongSearch.Search.Request(query: query)
        interactor?.search(request: request)
    }
    
    func recent(){
        activityIndicator.startAnimating()
        let request = SongSearch.Recent.Request()
        interactor?.recent(request: request)
    }
    
    func songs(){
        activityIndicator.startAnimating()
        let request = SongSearch.Songs.Request()
        interactor?.songs(request: request)
    }
    
    var items: [SongItem] = []{
        didSet{
            collectionView.reloadData()
        }
    }
    
    var imageCacheManager = ImageCacheManager()
    var musicPlayerManager = MusicPlayerManager()
}

extension SongSearchViewController : SongSearchDisplayLogic{
    func displayRecent(viewModel: SongSearch.Recent.ViewModel) {
        activityIndicator.stopAnimating()
        self.items = viewModel.items
    }
    
    func displaySearchResults(viewModel: SongSearch.Search.ViewModel)
    {
        activityIndicator.stopAnimating()
        self.items = viewModel.items
    }
    
    func displaySongs(viewModel: SongSearch.Songs.ViewModel)
    {
        activityIndicator.stopAnimating()
        self.items = viewModel.items
    }
}

extension SongSearchViewController: UISearchResultsUpdating, UISearchBarDelegate {
    // MARK: - UISearchResultsUpdating Delegate
    func updateSearchResults(for searchController: UISearchController) {
        
    }
    
    func searchBarSearchButtonClicked(_ searchBar: UISearchBar) {
        if let text = searchController.searchBar.text, text != ""{
            self.search(query: text)
        }
        else{
            self.recent()
        }
    }
}

extension SongSearchViewController: UICollectionViewDelegate, UICollectionViewDataSource {
    func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
        let item = items[indexPath.row]
        print("romikabitag: name=\(item.name);id=\(item.id)")
        router?.routeToLevelSelect(segue: nil)
    }
    
    func collectionView(_ collectionView: UICollectionView, numberOfItemsInSection section: Int) -> Int {
        return items.count
    }
    
    func collectionView(_ collectionView: UICollectionView, cellForItemAt indexPath: IndexPath) -> UICollectionViewCell {
        if let cell = collectionView.dequeueReusableCell(withReuseIdentifier: ReuseIdentifiers.SongSearchCell, for: indexPath) as? SongSearchCell{
            
            let item = items[indexPath.row]
            
            cell.item = item
            
            if let image = imageCacheManager.cachedImage(url: item.url) {
                // Cached: set immediately.
                
                cell.imageView.image = image
                cell.imageView.alpha = 1
            } else {
                // Not cached, so load then fade it in.
                cell.imageView.alpha = 0
                
                imageCacheManager.fetchImage(url: item.url, completion: { (image) in
                    // Check the cell hasn't recycled while loading.
                    if (cell.item?.id ?? "") == item.id {
                        cell.imageView.image = image
                        UIView.animate(withDuration: 0.2) {
                            cell.imageView.alpha = 1
                        }
                    }
                })
            }
            
            cell.artistLabel.text = item.artistName
            cell.titleLabel.text = item.name
            cell.albumLabel.text = item.albumName
            
            return cell
        }
        return UICollectionViewCell()
    }
    
    
}
